import $ from 'jquery';

const videoWithRaj = document.getElementById('videoWithRaj');
	const videoWithAmit = document.getElementById('videoWithAmit');
	const videoWithShefali = document.getElementById('videoWithShefali');
	const errorMsgElement = document.getElementById('snap#ErrorMsg');
	const constraint = {
			audio: false,
			video: {
				width: 360, height: 420
			}
	};
	var stream;
	//Access Webcam
	async function initOfRaj(){
		try{
			stream = await navigator.mediaDevices.getUserMedia(constraint);
			handleSuccessOfRaj(stream);
		}catch(e){
			errorMsgElement.innerHTML = `navigator.getUserMedia.error:${e.toString()}`;
		}
	}
	async function initOfAmit(){
		try{
			const stream = await navigator.mediaDevices.getUserMedia(constraint);
			handleSuccessOfAmit(stream);
		}catch(e){
			errorMsgElement.innerHTML = `navigator.getUserMedia.error:${e.toString()}`;
		}
	}
	async function initOfShefali(){
		try{
			const stream = await navigator.mediaDevices.getUserMedia(constraint);
			handleSuccessOfShefali(stream);
		}catch(e){
			errorMsgElement.innerHTML = `navigator.getUserMedia.error:${e.toString()}`;
		}
	}

	
	//Success
	function handleSuccessOfRaj(stream){
		$("#loaderRaj").hide();
		window.stream = stream;
		videoWithRaj.srcObject = stream;
	}
	function handleSuccessOfAmit(stream){
		$("#loaderAmit").hide();
		window.stream = stream;
		videoWithAmit.srcObject = stream;
	}
	function handleSuccessOfShefali(stream){
		$("#loaderShefali").hide();
		window.stream = stream;
		videoWithShefali.srcObject = stream;
	}
	
	//Load init
	
	$(document).ready(function(){
		$('#startVideoWithRaj').click(function(){
			$("#loaderRaj").show();
			initOfRaj();
		});
		$('#startVideoWithAmit').click(function(){
			$("#loaderAmit").show();
			initOfAmit();
		});
		$('#startVideoWithShefali').click(function(){
			$("#loaderShefali").show();
			initOfShefali();
		});
		$('#mic-on-raj').click(function() {
			$('#mic-off-raj').show();
			$(this).hide();
		});
		$('#mic-off-raj').click(function() {
			$('#mic-on-raj').show();
			$(this).hide();
		});
		$('#video-on-raj').click(function() {
			$('#video-off-raj').show();
			$('#unavailableRaj').show();
			$('#videoWithRaj').hide();
			stream.getTracks().forEach(function(track) {
				  track.stop();
				});
			$(this).hide();
		});
		$('#video-off-raj').click(function() {
			$('#video-on-raj').show();
			$('#unavailableRaj').hide();
			$('#videoWithRaj').show();
			initOfRaj();
			$(this).hide();
		});
		$('#mic-on-amit').click(function() {
			$('#mic-off-amit').show();
			$(this).hide();
		});
		$('#mic-off-amit').click(function() {
			$('#mic-on-amit').show();
			$(this).hide();
		});
		$('#video-on-amit').click(function() {
			$('#video-off-amit').show();
			$('#unavailableAmit').show();
			$('#videoWithAmit').hide();
			stream.getTracks().forEach(function(track) {
				  track.stop();
				});
			$(this).hide();
		});
		$('#video-off-amit').click(function() {
			$('#video-on-amit').show();
			$('#unavailableAmit').hide();
			$('#videoWithAmit').show();
			initOfAmit();
			$(this).hide();
		});
		$('#mic-on-shefali').click(function() {
			$('#mic-off-shefali').show();
			$(this).hide();
		});
		$('#mic-off-shefali').click(function() {
			$('#mic-on-shefali').show();
			$(this).hide();
		});
		$('#video-on-shefali').click(function() {
			$('#video-off-shefali').show();
			$('#unavailableShefali').show();
			$('#videoWithShefali').hide();
			stream.getTracks().forEach(function(track) {
				  track.stop();
				});
			$(this).hide();
		});
		$('#video-off-shefali').click(function() {
			$('#video-on-shefali').show();
			$('#unavailableShefali').hide();
			$('#videoWithShefali').show();
			initOfShefali();
			$(this).hide();
		});
	});
