import React, { useEffect, useState, useRef }  from 'react';

class Modal extends React.Component {
    componentDidMount () {
        const videoRef = useRef(null);
    const {image} = this.props.location.aboutProps;
    useEffect(() => {
        getVideo();
      });
    const getVideo = () => {
    navigator.mediaDevices
      .getUserMedia({ video: { width: 360, height: 420 } })
      .then(stream => {
        let video = videoRef.current;
        video.srcObject = stream;
        video.play();
      })
      .catch(err => {
        console.error("error:", err);
      });
  };
    }
    render () {
    return (
        <>

            <div className="container mt-3" style={{border: '1px solid black', boxShadow: '4px 5px 4px grey', backgroundColor: 'silver'}}>
      	<div className="row" style={{padding:'6%'}}>
      		<div className="col-6 " style={{backgroundImage: image, opacity:'0.6', backgroundPosition:'center', backgroundSize:'cover', width:'100%', height:'420px'}}>
  				<p style={{position:'absolute', top:'170px', left:'100px', fontWeight:'bold'}}><span className="fas fa-sync fa-2x fa-spin"></span> Connecting</p>	
      		</div>
      		<div className="col-6">
      				<div className="text-center pt-4" id="loaderRaj" style={{display:'none'}}>
  						<span className="fa fa-refresh fa-spin fa-2x "></span><br/>
  						<h5>Connecting</h5>
  					</div>
      				<div className="video-wrap" id="camera">
					  <video ref={videoRef}  />
						  {/* <video id="videoWithRaj" playsinline autoplay></video> */}
      					<img id="unavailableRaj" style={{display:'none'}} src="https://images.drivereasy.com/wp-content/uploads/2017/04/1-14.jpg" width="360px" height="420px"/>
      				</div>
      		</div>
      	</div>
      	<div className="row" style={{paddingBottom:'4%'}}>
      		<div className="col-4"></div>
      		<div className="col-1 ">
      			<span id="mic-on-raj" className="fas fa-microphone-alt fa-2x"  style={{cursor:'pointer', color:'steelblue'}}></span>
      			<span id="mic-off-raj" className="fas fa-microphone-alt-slash fa-2x"  style={{display:'none', cursor:'pointer', color:'steelblue'}}></span>
      		</div>
      		
      		<div className="col-1">
      			<button className="close" closeButton data-dismiss="modal" aria-label="Close"><span id="call-on-raj" className="fas fa-phone fa-2x"  style={{cursor:'pointer', color:'rgb(207, 19, 19)'}}></span></button>
      		</div>
      		
      		<div className="col-1 ml-3">
      			<span id="video-on-raj" className="fas fa-video fa-2x"  style={{cursor:'pointer', color:'steelblue'}}></span>
      			<span id="video-off-raj" className="fas fa-video-slash fa-2x"  style={{display:'none', cursor:'pointer', color:'steelblue'}}></span>
      		</div>
      	</div>
      </div>
        </>
    );
}
}

export default Modal;
